WHERE USER_MNG_NO = #{userMngNo,jdbcType=VARCHAR}

UPDATE M_AUTH
LOGIN_FAILED_COUNT = 0, UPDATED_USER_ID = #{updatedUserId,jdbcType=VARCHAR}, UPDATED_DATE = #{updatedDate,jdbcType=TIMESTAMP},
WHERE USER_MNG_NO = #{userMngNo,jdbcType=VARCHAR}

UPDATE M_AUTH
LOGIN_FAILED_COUNT = LOGIN_FAILED_COUNT + 1, UPDATED_USER_ID = #{updatedUserId,jdbcType=VARCHAR}, UPDATED_DATE = #{updatedDate,jdbcType=TIMESTAMP},
WHERE USER_MNG_NO = #{userMngNo,jdbcType=VARCHAR}

UPDATE M_AUTH
ACCOUNT_LOCK_FLAG = 'Y', ACCOUNT_LOCK_DATE = #{accountLockDate,jdbcType=TIMESTAMP}, UPDATED_USER_ID = #{updatedUserId,jdbcType=VARCHAR}, UPDATED_DATE = #{updatedDate,jdbcType=TIMESTAMP},
WHERE USER_MNG_NO = #{userMngNo,jdbcType=VARCHAR}

